package com.xfb.xinfubao.payment;import android.text.TextUtils;import java.util.HashMap;import java.util.Map;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(String rawResult) {		if (rawResult == null) {			return;		}		String []results = rawResult.split(";");		Map<String,String> map = new HashMap();		for (int i = 0 ;i < results.length;i++){			String []keyValus = results[i].split("=");			map.put(keyValus[0],keyValus[1]);		}		for (String key : map.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = map.get(key);			} else if (TextUtils.equals(key, "result")) {				result = map.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = map.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}